import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

export default function StudentTable() {
  const [students, setStudents] = useState("");

  useEffect(() => {
    fetch('http://localhost:8000/students')
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setStudents(data);
      }).catch((err) => {
        console.log(err.message);
      });
  }, []);

  return (
    <div className="container">
      <h2>Student Records</h2>
      <div className="table-container">
        <Link to="/student/create" className="btn btn-add">Add New Student</Link>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Place</th>
              <th>Phone</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {
              Array.isArray(students) && students.map((item) => (
                <tr key={item.id}>
                  <td>{item.id}</td>
                  <td>{item.name}</td>
                  <td>{item.place}</td>
                  <td>{item.phone}</td>
                  <td>
                    <Link to={`/student/view/${item.id}`} className="btn btn-view">View</Link>
                    <Link to={`/student/edit/${item.id}`} className="btn btn-edit">Edit</Link>
                    <button className="btn btn-delete" onClick={() => alert('Delete clicked')}>Delete</button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </table>
      </div>
    </div>
  );
}
